    public int minimumSum(int[] nums) {
        int sum = Integer.MAX_VALUE;
        for (int i = 0; i < nums.length - 2; i++) {
            if (nums[i] == nums[i + 1]) {
                continue;
            }
            int potentialMinSum = nums[i];
            if (potentialMinSum >= sum) {
                continue;
            }
            for (int j = i + 1; j < nums.length - 1; j++) {
                if (nums[j] == nums[j + 1]) {
                    continue;
                }
                if (nums[i] >= nums[j]) {
                    continue;
                }
                potentialMinSum = nums[i] + nums[j];
                if (potentialMinSum >= sum) {
                    continue;
                }
                for (int k = j + 1; k < nums.length; k++) {
                    if (nums[j] <= nums[k]) {
                        continue;
                    }
                    potentialMinSum = nums[i] + nums[j] + nums[k];
                    if (potentialMinSum < sum) {
                        sum = potentialMinSum;
                    }
                }
            }
        }
        return sum == Integer.MAX_VALUE ? -1 : sum;
    }
